from Cryptodome.Util.number import getPrime
from CCCTF import FLAG

p = getPrime(512)
q = getPrime(512)
assert p != q
n = p * q
phi = (p-1) * (q-1)

e1 = getPrime(512)
e2 = getPrime(512)

d1 = pow(e1, -1, phi)
d2 = pow(e2, -1, phi)

f = int(FLAG.encode("utf-8").hex(), 16)

c1 = pow(f, e1, n)
c2 = pow(f, e2, n)

print({"n": n, "e1": e1, "e2": e2, "c1": c1, "c2":c2})

